sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var STAGE_LIBRARY_TABLE = 'x_431105_cand_ass_stage_library';
    var CANDIDATE_STAGE_TABLE = 'x_431105_cand_ass_publication_stage';

	var grCandidateStage = new GlideRecord(CANDIDATE_STAGE_TABLE);
    var grStageLibrary = new GlideRecord(STAGE_LIBRARY_TABLE);
	grStageLibrary.addEncodedQuery('parent!=NULL^open_to_other_institution=true');
    grStageLibrary.query();
    while (grStageLibrary.next()) { 
		grCandidateStage.initialize();
		grCandidateStage.publication = current.sys_id;
		grCandidateStage.stage = grStageLibrary.sys_id;
		grCandidateStage.order = grStageLibrary.order;
		grCandidateStage.insert();
    }
})(current, previous);
