sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var caUtils = new CandidateAssessmentUtils();
    var grCandidateSC = new GlideRecord(caUtils.CANDIDATE_SC);
    grCandidateSC.addQuery('candidate', current.candidate);
    grCandidateSC.addQuery('assessment_selection_criteria', current.assessment_selection_criteria);
    grCandidateSC.query();
    if (grCandidateSC.next()){
        grCandidateSC.score = caUtils.calculateSCScoreForCBT(grCandidateSC);
	}
    grCandidateSC.update();
})(current, previous);
