sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var PENDING_ELIGIBILITY_CHECK = 50;
	var PENDING_ELIGIBILITY_APPROVAL = 60;
	var ELIGIBILITY_DONE = 70;
	var REQUESTED = 'requested';
	var NOT_YET_REQUESTED = 'not requested';
	var APPROVED = 'approved';
	var REJECTED = 'rejected';

	if (current.state == PENDING_ELIGIBILITY_CHECK && current.approval == REQUESTED){
		current.state = PENDING_ELIGIBILITY_APPROVAL;
		current.update();
	} else if (current.state == PENDING_ELIGIBILITY_APPROVAL && 
				(current.approval == APPROVED || current.approval == REJECTED)){
		current.state = ELIGIBILITY_DONE;
		current.update();
	}	
})(current, previous);
