sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var assessment = current.sys_id;
    var NOT_ELIGIBLE = '30';
    var caUtils = new CandidateAssessmentUtils();
    var grCandidate = new GlideRecord(caUtils.CANDIDATE_TABLE);
    grCandidate.addQuery('assessment', assessment);
    //grCandidate.addQuery('state', '!=', NOT_ELIGIBLE);
    grCandidate.query();
    while (grCandidate.next()) {
        caUtils.copyAssessmentSCInCandidate(assessment, grCandidate.sys_id);
        caUtils.populateCandidateQA(assessment, grCandidate.sys_id);
        grCandidate.state = caUtils.IN_ELIGIBILITY;
        grCandidate.update();
    }
})(current, previous);
